    function deserialize(MarketTypes.GetDealClientReturn memory ret, bytes memory rawResp) internal pure {
        uint byteIdx = 0;

        (ret.client, byteIdx) = rawResp.readUInt64(byteIdx);
    }
}

library GetDealProviderCBOR {
